import React, {Component} from 'react';
import './App.css';

export default class App extends Component{
    constructor(){
        super();

        this.state = {

            address: '',
            creditCard: '',

            cart: [],

            shirts: [{
                id:1, imageUrl:"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1550688238-theory-1550688225.jpg", title:"sampeleTitle", price:45, description:"sampleDescription", quantity: 0
                },
                {
                id:2, imageUrl:"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1550688238-theory-1550688225.jpg", title:"sampeleTitle1", price:45, description:"sampleDescription1", quantity: 0
                }],

            pants: [{
                id:3, imageUrl:"https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1550688238-theory-1550688225.jpg", title:"sampeleTitle2", price:45, description:"sampleDescription2", quantity: 0
                }]
        }

        this.addToCart = this.addToCart.bind(this);
        this.checkout = this.checkout.bind(this);
        this.handleAddressInput = this.handleAddressInput.bind(this);
        this.handleCreditCardInput = this.handleCreditCardInput.bind(this);
    }

    addToCart(product){
        this.setState({
            cart: [...this.state.cart, product],
        });

        for(let x=0; x<this.state.cart.length; x++){
            this.setState({
                cart: [...this.state.cart.quantity, 100],
            })
        }

        console.log(this.state.cart[0].quantity);
    }

    checkout(){
        if(this.state.address && this.state.creditCard){
            this.setState({ cart: [] });
            alert("Purchase is complete!");
        }
        else{
            alert("Please fill out the required fields!");
        }
        
    }

    handleAddressInput(event){
        this.setState({address: event});
    }

    handleCreditCardInput(event){
        this.setState({creditCard: event})
    }

    render(){
        return (
            <div className="App">
                <section className="products">
                <h1> Products</h1>
                <h2>Shirts</h2>
                {
                    this.state.shirts.map(product => (
                        <div key={product.id} className="product">
                            <img src={product.imageUrl} alt=""/>
                            <h4>{product.title}</h4>
                            <p>{product.description}</p>
                            <p>{product.price}</p>
                            <button onClick={() => this.addToCart(product)}> Add to cart </button>
                        </div>
                    ))
                    
                }

                <h2>Pants</h2>
                {
                    this.state.pants.map(product => (
                        <div key={product.id} className="product">
                            <img src={product.imageUrl} alt=""/>
                            <h4>{product.title}</h4>
                            <p>{product.description}</p>
                            <p>{product.price}</p>
                            <button onClick={() => this.addToCart(product)}> Add to cart </button>
                        </div>
                    ))
                    
                }
                </section>

                <section className="cart">
                <h1> Cart </h1>
                <h2> 
                    Total $ {this.state.cart.reduce((totalPrice, product) => 
                    (totalPrice += product.price), 0 )}
                </h2>
                <div>
                    <input type="text" placeholder="Address" value={this.state.address} onChange={e => this.handleAddressInput(e.target.value)}></input>
                    <input type="text" placeholder="Credit Card" value={this.state.creditCard} onChange={e => this.handleCreditCardInput(e.target.value)}></input>
                </div>
                <button onClick={this.checkout}>Checkout</button>

                {
                    this.state.cart.map(item => (
                        <div key={item.id} className="product">
                            <img src={item.imageUrl} alt=""/>
                            <h4>{item.title}</h4>
                            <p>{item.description}</p>
                            <p>{item.price}</p>
                        </div>
                    ))
                }
                </section>
            </div>
            
        )
    }
}
