import React, {Component} from 'react';
import './App.css';

export default class App extends Component{
    constructor(){
        super();

        this.state = {

            cart: [],

            shirts: [{
                id:1, imageUrl:"images/shirt1.jpg", title:"sampeleTitle", price:45, description:"sampleDescription"
                },
                {
                id:2, imageUrl:"images/shirt2.jpg", title:"sampeleTitle1", price:45, description:"sampleDescription1"
                }],

            pants: [{
                id:3, imageUrl:"images/pants.jpg", title:"sampeleTitle2", price:45, description:"sampleDescription2"
                }]
        }

        this.addToCart = this.addToCart.bind(this);
    }

    addToCart(product){
        this.setState({
            cart: [...this.state.cart, product],
        });
    }

    checkout(){
        this.setState({ cart: [] });
        alert("Purchase is complete!");
    }

    render(){
        return (
            <div className="App">
                <section className="products">
                <h1> Products</h1>
                <h2>Shirts</h2>
                {
                    this.state.shirts.map(product => (
                        <div key={product.id} className="product">
                            <img src={product.imageUrl} />
                            <h4>{product.title}</h4>
                            <p>{product.description}</p>
                            <p>{product.price}</p>
                            <button onClick={() => this.addToCart(product)}> Add to cart </button>
                        </div>
                    ))
                    
                }

                <h2>Pants</h2>
                {
                    this.state.pants.map(product => (
                        <div key={product.id} className="product">
                            <img src={product.imageUrl} />
                            <h4>{product.title}</h4>
                            <p>{product.description}</p>
                            <p>{product.price}</p>
                            <button onClick={() => this.addToCart(product)}> Add to cart </button>
                        </div>
                    ))
                    
                }
                </section>

                <section className="cart">
                <h1> Cart </h1>
                <h2> 
                    Total $ {this.state.cart.reduce((totalPrice, product) => 
                    (totalPrice += product.price), 0 )}
                </h2>
                <button onClick={this.checkout}>Checkout</button>

                {
                    this.state.cart.map(item => (
                        <div key={item.id} className="product">
                            <img src={item.imageUrl} />
                            <h4>{item.title}</h4>
                            <p>{item.description}</p>
                            <p>{item.price}</p>
                        </div>
                    ))
                }
                </section>
            </div>
            
        )
    }
}
